import React, { Component } from "react"
import TodoForm from "./TodoForm"
import TodoItem from "./TodoItem"
import  './index.css';


class App extends Component{
    constructor(){
        super()
        this.state ={
            todo:"",
            todos:[]
        } 
        this.addTodo =this.addTodo.bind(this)
        this.handleCheckChange =this.handleCheckChange.bind(this)
    }
    addTodo(todo){
      this.setState({
          todos:[todo,...this.state.todos]
      })
    }
    handleCheckChange(id){
        this.setState(prevState=>{
        const updatedTodos = prevState.todos.map(todo=>{
                    if(todo.id === id){
                        return{ 
                            ...todo,
                        complete:!todo.complete
                        }

                    }
                    return todo
            })
            console.log(prevState.todos)
            console.log('>>>.')
            console.log(updatedTodos)
            return {
                todos : updatedTodos
            }
        })
    }
    componentDidMount(){
        const todoList = JSON.parse(localStorage.getItem("data"))
        console.log(todoList)
        this.setState({
            todos : todoList
        })

    }
    componentDidUpdate(prevProps,prevState){
        if(prevState.todos.length !== this.state.todos.length){
            const jsonTodo = JSON.stringify(this.state.todos)
            localStorage.setItem("data",jsonTodo)
        }

    }

    render(){
        const Todos = this.state.todos.map(todo =>{
                return (
                    <TodoItem key={todo.id} todo={todo} handleCheckChange={this.handleCheckChange}/>  
                )
         })
        return(
            <div className="todo-list">
                <TodoForm onSubmit={this.addTodo}/>
                {Todos}

            </div>
        )
    }
}
export default App;
