import React, { Component } from "react"

function InputTodo(props){

    return(
        <div>
             <input type="text" placeholder="Enter your todo" onChange={props.handleChange}/>
             <button onClick={props.handleClick}>Add</button>
        </div>
    )

}
function List(props){

    return(
        <div>
            <input type="checkbox" />
            <span>{props.value}</span>
        </div>

    )
}

class App extends Component{
    constructor(){
        super()
        this.state ={
            todo:"",
            done:false
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClick = this.handleClick.bind(this)
    }
    handleChange(event){
        this.setState({
            todo:event.target.value
        })
    }
    handleClick(event){
        this.setState(prevState=>{
            this.state.done =!prevState
        })
        console.log(prevState)

    }

    render(){
        return(
            <div>
                <InputTodo  handleChange={this.handleChange} handleClick={this.handleClick}/>
                {this.state.done? "<List />":null}
            </div>
        )
    }
}
export default App;
