import React from "react"

function TodoItem(props){
    const completedStyle = {
        fontStyle:"italics",
        color:"#8893B8",
        textDecoration:"line-through"
    }
    return(
        <div className="todo-item">
                <input type="checkbox" id={props.todo.id} onChange={()=>{props.handleCheckChange(props.todo.id)}} checked={props.todo.complete}/>
                <p for={props.todo.id} style={props.todo.complete? completedStyle : null}>{props.todo.text}</p>
         </div>
    )
}
export default TodoItem
