import React from "react"

function TodoItem(props){
    const completedStyle = {
        fontStyle:"italics",
        color:"#cdcdcd",
        textDecoration:"line-throught"
    }
    return(
        <div className="todo-item">
                <input type="checkbox" onChange={()=>{props.handleCheckChange(props.todo.id)}} />
                <p style={props.todo.complete? completedStyle : none}>{props.todo.text}</p>
         </div>
    )
}
export default TodoItem
