import React, { Component } from "react"

function InputTodo(props){

    return(
        <div>
             <input type="text" placeholder="Enter your todo" onChange={props.handleChange}/>
             <button>Add</button>
        </div>
    )

}
function List(props){

    return(
        <div>
            <input type="checkbox" />
            <span>{props.value}</span>
        </div>

    )
}

class App extends Component{
    constructor(){
        super()
        this.state ={
            todo:""
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClick = this.handleClick.bind(this)
    }
    handleChange(event){
        this.setState({
            todo:event.target.value
        })
    }

    render(){
        console.log(this.state)
        return(
            <div>
                <InputTodo  handleChange={this.handleChange} />
                <List value={this.state.todo} />
            </div>
        )
    }
}
export default App;
